function burgerMenu(){const e=document.querySelector(".burger"),s=document.querySelector(".menu"),t=document.querySelector("body"),c=document.querySelector(".menu");e.addEventListener("click",(()=>{s.classList.contains("active")?(s.classList.remove("active"),e.classList.remove("active-burger"),t.classList.remove("locked")):(s.classList.add("active"),e.classList.add("active-burger"),t.classList.add("locked"))})),c.addEventListener("click",(c=>{c.target.classList.contains("menu__item-link")&&(s.classList.remove("active"),e.classList.remove("active-burger"),t.classList.remove("locked"))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(s.classList.remove("active"),e.classList.remove("active-burger"),t.classList.remove("locked"))}))}burgerMenu();